import React, { Component } from 'react';
import Registration_account  from "./Registration/Registration_account"; 
class Registration extends Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            currentStep: 1,
            username: '', 
            lastname:'', 
            password:'', 
            account: null, 

        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange = async(event) => {

        const {name, value} = event.target
        this.setState({
          [name]: value, 
        })    
      }

    componentWillMount()
    {
      /*  fetch('/ApiAutomationHost/rest/account/account/registration')
        .then (response => {return response.json()})
                .then(data => this.setState({data}, console.log(data)))*/
    }
    _next()
    {
      let currentStep = this.state.currentStep; 

      currentStep = currentStep >= 1?3 : currentStep + 1;  
      this.setState(
        {
          currentStep:currentStep
        }
      )
    }

    _prev(){
        let currentStep = this.state.currentStep; 
        currentStep = currentStep <= 1? 1 : currentStep - 1; 

        this.setState(
          {
            currentStep: currentStep 
          }
        )

    }

    get previousButton()
    {
      let currentStep = this.state.currentStep; 

      if (currentStep !== 1)
      {
        return(
          <button
          className="btn btn-secondary"
          type="button"
          onClick={this._prev}
          >
            previous
          </button>
        )

      }
      return null; 
    }

    get nextButton(){
      let currentStep = this.state.currentStep; 

      if (currentStep < 3)
      {
        return (
          <button
          className="btn btn-primary float-right"
            type="button"
            onClick={this._next}
          >
          next
          </button>
         
        )
      }
      return null; 

    }


    render()
    {
        return(
            <div>

            <h1>Registration</h1>
            <form>
            
         
              
              <Registration_account
              currentStep = {this.state.currentStep}
              handleChange = {(event) => this.handleChange(event)}
              
              account = {this.state.account}
              lastname = {this.state.lastname}
              />


              </form>
            </div>


        );

    }

}

export default Registration;
