import React, { Component } from 'react';
import { Form, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
class Login extends Component
{
    constructor()
    {
        super()
        this.state = {
            data : [],
            'username':'',
            'password':'',
            validate: {
                passwordState:'', 
                emailState: ''
            }

        }
    }
    handleChange = async(event) => {
        const {target} = event; 
        const value = target.type === 'checkbox' ? target.checked : target.value; 
        const { name } = target;
         this.setState({
            [name]: value,
            })
    }
    validateEmail(event){
        const emailRex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        const {validate} = this.state; 
        if (emailRex.test(event.target.value))
        {
            validate.emailState = 'has-success';    
        }
        else 
        {
            validate.emailState = 'has-danger'
        }


    }
    handleLoginSubmit = (event) => {

        const details = Object.create(
            null,
            {
                username :{value: this.state.username,enumerable: true } ,
                password: {value : this.state.password, enumerable: true}

            }
        )

        fetch('/ApiAutomationHost/rest/authentication/login',
        {
            method: 'POST',
            headers: new Headers({
                'Accept': 'application/json, text/plain, */*',
                'Content-type': 'application/json',
                'Access-Control-Allow-Origin': '*'
             }),
             body:JSON.stringify( details)
        }
        )
        .then ((response) => {response.json()})
                .then((data) => this.setState({data}, console.log(data)))


    }
    componentWillMount()
    {
      

    }
    render()
    {
        return(
            <div>
            <h3 className="panel-title">
              LOGIN PAGE
            </h3>
                <Form>
                    <FormGroup row> 
                        <label sm={2} md={{ size: 6, offset: 3 }}>username</label>    
                        <input 
                        type="username"
                         name="username"
                         id ="username"
                         placeholder="username"
                         value ={this.state.username}
                         onChange = {(event)=> this.handleChange(event)}
                        
                        />                
                    </FormGroup>
                    <FormGroup row>
                    <label sm={2} md={{ size: 6, offset: 3 }}>password</label>    
                        <input 
                        type="password"
                         name="password"
                         id ="password"
                         placeholder="password"
                         value = {this.state.password}
                         onChange = {(event)=> this.handleChange(event)}
                        /> 
                        </FormGroup>

                    </Form>
            
            </div>
        );

    }

}
export default Login;
