import React ,{ Component } from 'react';
import './Css/Forgotpassword.css';
import Username from './Forgotpasswordcomponents/Username'; 
import Account from './Forgotpasswordcomponents/Account'; 


import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';
  const url_Allaccount = 'ApiAutomationHost/rest/account/account/Userdata'; 
  const update_Passwordurl = ''; 
class Forgotpassword extends Component
{
    constructor(props)
    {
        super(props); 

        this.state = {
            accountid : '', 
            newpassword :'', 
            step: 1, 
            User_data: []

        }
        this.handleChange = this.handleChange.bind(this); 
      //  this.nextButton = this.nextButton.bind(this); 
    }
    handleChange = async(event) => {

        const {name, value} = event.target
        
        this.setState({
          [name]: value, 
        })    
      }


    alluserapi = () => 
    {
        fetch(url_Allaccount)
        .then(response => {return response.json()})
        .then(data => this.setState({User_data:data}, console.log(data)) )

    }
  UpdatePassword = () => 
  {
    var id = this. state.accountid; 
    fetch(update_Passwordurl +id )
  }


    checkuserexist = () => 
    {
        console.log(this.state);
        console.log('check')
        var  data  = this.state.User_data; 
        var id = this.state.accountid; 
        for(var i =0; i < data.length; i++)
        {
           if (data[i].account_number == id )
           {
                return true; 
           }
           else 
           {
               return false; 
               
           }
        }
    }


    _next = () => 
    {
        let step = this.state.step; 
        step = step >= 1?2 : step + 1;
        this.setState(
            {
              step: step 
            }
          )


    }
    nextButton = () => 
    {
         
       
        if ( this.checkuserexist() === true)
        {
           
                this._next(this); 
        }
        else
        {
           console.log("failed")     
        } 
    }



    componentDidMount()
    {
        this.alluserapi(); 
       
    }
    componentDidUpdate()
    {
    
    }

   
        

    render()
    {
        const {step} = this.state;
            switch(step)
           {
                case 1: 
                return < Username

                         handleChange = {(event) => this.handleChange(event)}  
                    
                         accountid ={this.state.accountid}
                         nextButton = {this.nextButton}


                 /> 
                 case 2: 
                 return <Account 
                 
                 />

           }

            
    }
}
export default Forgotpassword ;
