import React, { Component } from 'react';
import { Form, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import {FormFeedback,Col} from 'reactstrap'; 
import { Link } from 'react-router-dom'; 
class Login extends Component
{
    constructor()
    {
        super()
        this.state = {
            data : [],
            'username':'',
            'password':'',
            validate: {
                passwordState:'', 
                usernameState: ''
            }

        }
    }
    handleChange = async(event) => {
        const {target} = event; 
        const value = target.type === 'checkbox' ? target.checked : target.value; 
        const { name } = target;
         this.setState({
            [name]: value,
            })
    }
    validateUsername(event){
        const usernameRex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        const {validate} = this.state; 
        if (usernameRex.test(event.target.value))
        {
            validate.usernameState = 'has-success';    
        }
        else 
        {
            validate.usernameState = 'has-danger'
        }
        this.setState({ validate })


    }
    validatePassword = (event)=>
    {
        const passwordRex =  /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{5,}$/;
        const { validate } = this.state; 
            if (passwordRex.test(event.target.value)) {
            validate.passwordState = 'has-success'
            } else {
            validate.passwordState = 'has-danger'
            }
            this.setState({validate})
    }
    handleLoginSubmit = (event) => {

        const details = Object.create(
            null,
            {
                username :{value: this.state.username, enumerable: true } ,
                password: {value : this.state.password, enumerable: true}

            }
        )

        fetch('/ApiAutomationHost/rest/authentication/login',
        {
            method: 'POST',
            headers: new Headers({
                'Accept': 'application/json, text/plain, */*',
                'Content-type': 'application/json',
                'Access-Control-Allow-Origin': '*'
             }),
             body:JSON.stringify( details)
        }
        )
        .then ((response) => {response.json()})
                .then((data) => this.props.history.push(
                    {
                        pathname :'/', 
                        details : data
                    }
                )); 


    }
    
    submitForm = (e) =>{
        e.preventDefault();
        if(this.state.validate.emailState === 'has-success' )
            {
                this.handleLoginSubmit(e)
            }
        
}
    componentWillMount()
    {
      

    }
    render()
    {
        return(
            <div>
            <h3 className="panel-title">
              LOGIN PAGE
            </h3>
                <Form  onSubmit={(event) => this.submitForm(event)} >
                    <FormGroup > 
                        <label sm={2} md={{ size: 6, offset: 3 }}>username</label>    
                        <input 
                        type="username"
                         name="username"
                         id ="username"
                         placeholder="username"
                         value ={this.state.username}
                         onChange = {(event)=> this.handleChange(event) 
                                               }
                        
                        />                
                    </FormGroup>
                    <FormGroup >
                    <label sm={2} md={{ size: 6, offset: 3 }}>password</label>  
                       <Col sm={12} md={{ size: 6, offset: 3 }}>
                        <input 
                         type="password"
                         name="password"
                         id ="password"
                         placeholder="********"
                         value = {this.state.password}
                         onChange = {(event)=> {this.handleChange(event)
                                                this.validatePassword(event)}
                        }
                        valid={this.state.validate.passwordState ==='has-success'}
                        invalid={this.state.validate.passwordState ==='has-danger'}
                        /> 
                        <FormFeedback invalid>
                              Password requires 1 uppercase, 1 lowercase, and at least 5 characters
                        </FormFeedback>
                        </Col>
                        </FormGroup>
                             <center>
                                <Button type="submit" outline color="primary" size ="lg">Log on</Button>
                             </center>

                    </Form>
            
            </div>
        );

    }

}
export default Login;
