import React, { Component } from 'react';
//import { METHODS } from 'http';
import jwt_decode from 'jwt-decode';

class Homepage extends Component
{
    constructor(props)
    {
        super(props); 

        this.state = {
            data: [], 
        }


       
    }
    CallApi =() => 
    {
        let token = localStorage.getItem('User') || null; 
        if (token)
        {
            return fetch ('/ApiAutomationHost/rest/Secured/account/',
            {
                method: 'GET', 
                Accept: 'application/json',
                 'AUTHORIZATION':  '', 

            }).then(res => res.json())
            .then(data => 
                this.setState({data},console.log(data))

                ); 
        }
    }
    componentWillMount()
    {
        this.CallApi(); 
    }
    render()
    {
        var token =  localStorage.getItem('User').replace("Bearer ", ''); 
        var decoded = jwt_decode(token);
        console.log(decoded)
        return(
            <div>
            
            <h1>Hello, {this.props.details}</h1>
            </div>
        );

    }
}
export default Homepage;
