import React, { Component } from 'react';
import Registration_account  from "./Registration/Registration_account"; 
import RegistrationUsername from "./Registration/RegisterUsername"; 
class Registration extends Component
{
    constructor(props)
    {
        super(props)
        this.state = {
            currentStep: 1,
            username: '', 
            lastname:'', 
            password:'', 
            account: null, 
            User_data: []

        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange = async(event) => {

        const {name, value} = event.target
        this.setState({
          [name]: value, 
        })    
      }

    componentWillMount()
    {
      /*  fetch('/ApiAutomationHost/rest/account/account/registration')
        .then (response => {return response.json()})
                .then(data => this.setState({data}, console.log(data)))*/
    }

    Registration_checkdata = () => 
    {

    }

    _next()
    {
      let currentStep = this.state.currentStep; 

      currentStep = currentStep >= 2?3 : currentStep + 1;  
      this.setState(
        {
          currentStep:currentStep
        }
      )
    }


    Submit_Registration = (event) => 
    {
      event.preventDefault(); 
      var passencrpt = this.state.password; 

      const create_user = Object.create(
        null,
        {
          account_number:{value: this.state.account, enumerable:true}, 
          lastname :{value:this.state.lastname, enumerable: true},
          username :{value:this.state.username, enumerable: true},
          password:{value:passencrpt, enumerable:true}

        }); 

        console.log(create_user); 
    }

    _prev(){
        let currentStep = this.state.currentStep; 
        currentStep = currentStep <= 1? 1 : currentStep - 1; 

        this.setState(
          {
            currentStep: currentStep 
          }
        )
    }

     previousButton()
    {
      let currentStep = this.state.currentStep; 

      if (currentStep !== 1)
      {
        return(
          <button
          className="btn btn-secondary"
          type="button"
          onClick={this._prev.bind(this)}
          >
            previous
          </button>
        )
      }
      return null; 
    }

     nextButton(){
      let currentStep = this.state.currentStep; 

      if (currentStep < 3)
      {
        return (
          <button
          className="btn btn-primary float-right"
            type="button"
            onClick={this._next.bind(this)}
          >
          next
          </button>    
        )
      }
      return null; 

    }


    render()
    {
        return(
            <div>

            <h1>Registration</h1>
            <p>Step {this.state.currentStep} </p> 
            <form onSubmit={(event) => this.Submit_Registration(event)}>
            
              <Registration_account
              currentStep = {this.state.currentStep}
              handleChange = {(event) => this.handleChange(event)}  
              account = {this.props.account}
              lastname = {this.state.lastname}
              />

              <RegistrationUsername

               currentStep = {this.state.currentStep}
               handleChange = {(event) => this.handleChange(event)}
               username = {this.state.username}
               password = {this.state.password}
              
              />
              {this.previousButton()}
              {this.nextButton()}
              </form>
            </div>


        );

    }

}

export default Registration;
