import React, { Component } from 'react';
//import { METHODS } from 'http';

class Homepage extends Component
{
    constructor(props)
    {
        super(props); 

        this.state = {
            data: [], 
        }


       
    }
    CallApi =() => 
    {
        let token = localStorage.getItem('User') || null; 
        if (token)
        {
            return fetch ('/ApiAutomationHost/rest/Secured/registrationaccount',
            {
                method: 'GET', 
                Accept: 'application/json',
              'AUTHORIZATION':  token, 

            }).then(res => res.json())
            .then(data => 
                this.setState({data},console.log(data))

                ); 
        }
    }
    
    render()
    {
        console.log(localStorage.getItem('User'))
        return(
            <div>
            
            <h1>Hello, {this.props.details}</h1>
            </div>
        );

    }
}
export default Homepage;
