import React ,{ Component } from 'react';
import './Css/Forgotpassword.css';
import Username from './Forgotpasswordcomponents/Username'; 


import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';
  const url_Allaccount = 'ApiAutomationHost/rest/account/account/Userdata'; 
class Forgotpassword extends Component
{
    constructor(props)
    {
        super(props); 

        this.state = {
            accountid : null, 
            newpassword :'', 
            step: 1, 
            User_data: [], 

        }
        this.handleChange = this.handleChange.bind(this); 
    }
    handleChange = async(event) => {

        const {name, value} = event.target
        this.setState({
          [name]: value, 
        })    
      }


    alluserapi = () => 
    {
        fetch(url_Allaccount)
        .then(response => {return response.json()})
        .then(data => this.setState({User_data:data}) )
    }

    checkuserexist = () => 
    {
        console.log(this.state.User_data);
        console.log('check')
        var  data  = this.state.User_data; 
        for(var i =0; i < data.length; i++)
        {
            console.log(this.state.User_data); 
        }
    }



    componentWillMount()
    {
        this.alluserapi(); 
        this.checkuserexist();
    }

        

    render()
    {
        const {step} = this.state;
            switch(step)
           {
                case 1: 
                return < Username
                         handleChange = {(event) => this.handleChange(event)}  
                 /> 

           }

            
    }
}
export default Forgotpassword ;
